SELECT ACTION.QUANTITY, ACTION.TYPE, PLYR_TI.ID_TM FROM ACTION JOIN (PLAYER, TIME, PLYR_TI) ON (PLAYER.ID = @PLAYERID AND ACTION.ID_PLYR = PLAYER.ID AND ID_TIME = TIME.ID AND PLYR_TI.ID_PLYR = PLAYER.ID) WHERE ACTION.CAT='SCORE'
SELECT ACTION.QUANTITY, ACTION.TYPE, PLYR_TI.ROLE FROM ACTION JOIN (TIME, PLYR_TI, EVENT) ON (ID_TI = TIME.ID AND ACTION.ID_PLYR = PLYR_TI.ID_PLYR AND TIME.ID_EV = EVENT.ID AND EVENT.ID = @EVENTID) WHERE ACTION.CAT='SCORE'
