SELECT PLAYER.NAME, PLAYER.LNAME1, PLAYER.LNAME2, EVENT.STAGE, EVENT.NAME, EVENT.DATE FROM PLYR_TI JOIN (PLAYER, TIME, EVENT) ON (PLYR_TI.ID_PLYR=@PLYR AND PLYR_TI.ID_PLYR=PLAYER.ID AND PLYR_TI.ID_TI=TIME.ID AND TIME.ID_EV=EVENT.ID)