CREATE VIEW USER_INFO AS SELECT NAME, LNAME1, LNAME2, EMAIL, UNAME, PASS, ROLE, CITY, STREET, USER.NUM, STATE, COUNTRY, PHONES.NUM FROM USER JOIN PHONES ON (USER.ID=PHONES.ID_USER);
CREATE VIEW RESULT_INFO AS SELECT QUANTITY, TYPE, CONTEXT, CAT, TIME, PLAYER.NAME AS PLAYERNAME, PLAYER.LNAME1 AS PLAYERLNAME, PLAYER.STATUS AS PLAYERSTAT, EVENT.NAME AS EVENTNAME FROM ACTION JOIN (PLAYER, TIME, EVENT) ON (ACTION.ID_PLYR=PLAYER.ID AND ACTION.ID_TI=TIME.ID AND EVENT.ID=TIME.ID_EVENT);